--
-- Table structure for table `bB_posts`
--

CREATE TABLE IF NOT EXISTS `bB_posts` (
  `postid` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(255) NOT NULL DEFAULT '',
  `body` mediumtext NOT NULL,
  `posttime` int(11) NOT NULL DEFAULT '0',
  `modifytime` int(11) NOT NULL DEFAULT '0',
  `status` enum('live','draft') NOT NULL DEFAULT 'live',
  `modifier` varchar(30) NOT NULL DEFAULT '',
  `sections` varchar(255) NOT NULL DEFAULT '',
  `ownerid` int(10) NOT NULL DEFAULT '0',
  `hidefromhome` tinyint(1) NOT NULL DEFAULT '0',
  `allowcomments` enum('allow','timed','disallow') NOT NULL DEFAULT 'allow',
  `autodisabledate` int(11) NOT NULL DEFAULT '0',
  `commentcount` int(11) NOT NULL DEFAULT '0',
  PRIMARY KEY (`postid`),
  KEY `ownerid` (`ownerid`)
) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=2 ;

--
-- Dumping data for table `bB_posts`
--

INSERT INTO `bB_posts` (`postid`, `title`, `body`, `posttime`, `modifytime`, `status`, `modifier`, `sections`, `ownerid`, `hidefromhome`, `allowcomments`, `autodisabledate`, `commentcount`) VALUES
(1, 'First Post', '[b]This is the first post of bBlog.[/b]\r\n\r\nYou may delete this post in the admin section. Make sure you have deleted the install file and changed the admin password. \r\n\r\nBe sure to visit the [url=http://www.bblog.com/forum.php]bBlog forum[/url] if you have any questions, comments, bug reports etc. \r\n\r\nHappy bBlogging!', 1114580511, 1114580511, 'live', 'bbcode', '', 1, 0, 'allow', 0, 0);

