(75, 0, 'rss_63aa89c82788002a1fec57789d71ea66', 'Y', 1, 'O:9:"magpierss":17:{s:6:"parser";i:0;s:12:"current_item";a:0:{}s:5:"items";a:0:{}s:7:"channel";a:8:{s:9:"generator";s:15:"Technorati v1.0";s:9:"webmaster";s:43:"support@technorati.com (Technorati Support)";s:4:"docs";s:37:"http://blogs.law.harvard.edu/tech/rss";s:3:"ttl";s:2:"60";s:5:"title";s:70:"Technorati Search for: http://www.learning-mentors.net/talking2myself/";s:4:"link";s:90:"http://www.technorati.com/search/http%3A%2F%2Fwww.learning-mentors.net%2Ftalking2myself%2F";s:11:"description";s:75:"Technorati URL search for http://www.learning-mentors.net/talking2myself/. ";s:7:"tagline";s:75:"Technorati URL search for http://www.learning-mentors.net/talking2myself/. ";}s:9:"textinput";a:4:{s:5:"title";s:17:"Search Technorati";s:11:"description";s:43:"Search millions of blogs for the latest on:";s:4:"name";s:1:"s";s:4:"link";s:36:"http://www.technorati.com/search.php";}s:5:"image";a:3:{s:3:"url";s:50:"http://static.technorati.com/pix/logos/logo_sm.gif";s:5:"title";s:15:"Technorati logo";s:4:"link";s:25:"http://www.technorati.com";}s:9:"feed_type";s:3:"RSS";s:12:"feed_version";s:3:"2.0";s:5:"stack";a:0:{}s:9:"inchannel";b:0;s:6:"initem";b:0;s:9:"incontent";b:0;s:11:"intextinput";b:0;s:7:"inimage";b:0;s:13:"current_field";s:0:"";s:17:"current_namespace";b:0;s:19:"_CONTENT_CONSTRUCTS";a:6:{i:0;s:7:"content";i:1;s:7:"summary";i:2;s:4:"info";i:3;s:5:"title";i:4;s:7:"tagline";i:5;s:9:"copyright";}}', 20, 8, '', 1, 'no'),
